mapper.select:
SELECT com.*, (SELECT COUNT(*) FROM comment_yooka AS COT WHERE com.cm_no = COT.cm_no) AS cot_cnt, (select count(*) from updown_yooka updown where updown.cm_no = com.cm_no AND updown.cm_updownstatus = 1) AS likecnt, (select count(*) from updown_yooka updown where updown.cm_no = com.cm_no AND updown.cm_updownstatus = 2) AS hatecnt from community_yooka AS com
AND cm_title LIKE CONCAT('%',#{searchKeyword},'%') AND cm_content LIKE CONCAT('%',#{searchKeyword},'%') AND cm_writer LIKE CONCAT('%',#{searchKeyword},'%')
ORDER BY cm_no DESC limit #{listcnt} offset #{start}

SELECT com.*, (SELECT COUNT(*) FROM comment_noriter AS COT WHERE com.cm_no = COT.cm_no) AS cot_cnt, (select count(*) from updown_noriter updown where updown.cm_no = com.cm_no AND updown.cm_updownstatus = 1) AS likecnt, (select count(*) from updown_noriter updown where updown.cm_no = com.cm_no AND updown.cm_updownstatus = 2) AS hatecnt from community_noriter AS com
AND cm_title LIKE CONCAT('%',#{searchKeyword},'%') AND cm_content LIKE CONCAT('%',#{searchKeyword},'%') AND cm_writer LIKE CONCAT('%',#{searchKeyword},'%')
ORDER BY cm_no DESC limit #{listcnt} offset #{start}

SELECT com.*, (SELECT COUNT(*) FROM comment_shopping AS COT WHERE com.cm_no = COT.cm_no) AS cot_cnt, (select count(*) from updown_shopping updown where updown.cm_no = com.cm_no AND updown.cm_updownstatus = 1) AS likecnt, (select count(*) from updown_shopping updown where updown.cm_no = com.cm_no AND updown.cm_updownstatus = 2) AS hatecnt from community_shopping AS com
AND cm_title LIKE CONCAT('%',#{searchKeyword},'%') AND cm_content LIKE CONCAT('%',#{searchKeyword},'%') AND cm_writer LIKE CONCAT('%',#{searchKeyword},'%')
ORDER BY cm_no DESC limit #{listcnt} offset #{start}

mapper.update:
update community_yooka
set cm_title = #{cm_title} ,cm_writer = #{cm_writer}, cm_content = #{cm_content}, cm_img = #{cm_img}
where cm_no = #{cm_no}

update community_noriter
set cm_title = #{cm_title} ,
cm_writer = #{cm_writer}, cm_content = #{cm_content}, cm_img =
#{cm_img}
where cm_no = #{cm_no}

update community_shopping
set cm_title = #{cm_title} ,
cm_writer = #{cm_writer}, cm_content = #{cm_content}, cm_img =
#{cm_img}
where cm_no = #{cm_no}

update updown_yooka set cm_up = cm_up + 1 where
cm_no
= #{cm_no}

update updown_yooka set cm_down = cm_down + 1
where
cm_no = #{cm_no}

update updown_noriter set cm_down = cm_down + 1
where
cm_no = #{cm_no}

