mapper.select:
SELECT COALESCE(SUM(P_TOTAL), 0)
FROM CART WHERE  
U_ID= #{u_id}
and c_payment="preparation"; 
and c_payment="ready";

SELECT *
FROM CART WHERE  C_NO=#{c_no}
AND U_ID= #{u_id}

mapper.update:
UPDATE cart
SET
P_COUNT=#{p_count}
WHERE
p_no = #{p_no}
and u_id=#{u_id}
and c_payment="preparation"; 
and c_payment="ready";

UPDATE CART
SET
P_TOTAL=#{p_count}*#{p_price}
WHERE
P_NO = #{p_no}
and u_id=#{u_id}
and c_no=#{c_no}

mapper.select:
	SELECT P_TOTAL FROM CART WHERE
	 P_NO=#{p_no} and
	 u_id=#{u_id}
 	and c_no=#{c_no}

mapper.delete:
DELETE FROM cart WHERE
c_no = #{c_no} and
u_id =#{u_id}
